
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// expression = operands & operators
		// operators = + - * / %
		// operands = values, variables, numbers, quantity
		
		int friends = 10;
		
		System.out.println(friends);
		friends++;
		System.out.println(friends);
		++friends;
		System.out.println(friends);
		friends+=2;
		System.out.println(friends);
		System.out.println((double)friends/5);

	}

}
